import React, { useState } from "react";
import axios from "axios";
import "./MedsForm.css"; // Import a CSS file for styling

const MedsForm = () => {
  const [medicineData, setMedicineData] = useState({
    medId: "",
    medName: "",
    genericName: "",
    medType: "",
    manufacturer: "",
    dosage: "",
    ageGroup: "",
    formulation: "",
    netContent: "",
    indication: "",
    sideEffects: "",
    availability: true,
    image: "",
    imageWidth: null,
    imageHeight: null,
  });

  const handleImageLoad = () => {
    const img = new Image();
    img.src = medicineData.image;
    img.onload = () => {
      setMedicineData({
        ...medicineData,
        imageWidth: img.width,
        imageHeight: img.height,
      });
    };
  };

  const handleChange = (event) => {
    const { name, value, type, checked } = event.target;

    // Use the "checked" property for checkboxes
    const newValue = type === "checkbox" ? checked : value;

    setMedicineData({ ...medicineData, [name]: newValue });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      await axios.post("http://127.0.0.1:8000/app/meds/", medicineData);
      alert("Medicine added successfully!");
      setMedicineData({
        medId: "",
        medName: "",
        genericName: "",
        medType: "",
        manufacturer: "",
        dosage: "",
        ageGroup: "",
        formulation: "",
        netContent: "",
        indication: "",
        sideEffects: "",
        availability: true,
        image: "",
        imageWidth: null,
        imageHeight: null,
      });
    } catch (error) {
      console.error("Error adding medicine:", error);
      alert("Error adding medicine. Please try again.");
    }
  };

  return (
    <div className="meds-form-container">
      <h2>Add Medicine</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Image URL:</label>
          <input
            type="text"
            name="image"
            value={medicineData.image}
            onChange={handleChange}
            onBlur={handleImageLoad}
          />
          {medicineData.imageWidth && medicineData.imageHeight && (
            <p>
              Image Dimensions: {medicineData.imageWidth} x{" "}
              {medicineData.imageHeight}
            </p>
          )}
        </div>

        <div className="form-group">
          <label>Medicine Name:</label>
          <input
            type="text"
            name="medName"
            value={medicineData.medName}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label>Generic Name:</label>
          <input
            type="text"
            name="genericName"
            value={medicineData.genericName}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label>Medicine Type:</label>
          <select
            name="medType"
            value={medicineData.medType}
            onChange={handleChange}
          >
            <option value="">Select Medicine Type</option>
            <option value="Tablet">Tablet</option>
            <option value="Capsule">Capsule</option>
            <option value="Liquid">Liquid</option>
          </select>
        </div>

        {/* ... rest of the form ... */}

        <button type="submit">Add Medicine</button>
      </form>
    </div>
  );
};

export default MedsForm;
