import React, { useState } from "react";
import axios from "axios";
import "./MedsForm.css"; // Import a CSS file for styling

const MedsForm = () => {
  const [medicineData, setMedicineData] = useState({
    medId: "",
    medName: "",
    genericName: "",
    medType: "",
    manufacturer: "",
    dosage: "",
    ageGroup: "",
    formulation: "",
    netContent: "",
    indication: "",
    sideEffects: "",
    availability: true,
    image: "",
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setMedicineData({ ...medicineData, [name]: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      await axios.post("http://127.0.0.1:8000/app/meds/", medicineData);
      alert("Medicine added successfully!");
      setMedicineData({
        medId: "",
        medName: "",
        genericName: "",
        medType: "",
        manufacturer: "",
        dosage: "",
        ageGroup: "",
        formulation: "",
        netContent: "",
        indication: "",
        sideEffects: "",
        availability: true,
        image: "",
      });
    } catch (error) {
      console.error("Error adding medicine:", error);
      alert("Error adding medicine. Please try again.");
    }
  };

  return (
    <div className="meds-form-container">
      <h2>Add Medicine</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <input
            type="text"
            name="medName"
            placeholder="Medicine Name"
            value={medicineData.medName}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <input
            type="text"
            name="genericName"
            placeholder="Generic Name"
            value={medicineData.genericName}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <select
            name="medType"
            value={medicineData.medType}
            onChange={handleChange}
          >
            <option value="">Select Medicine Type</option>
            <option value="Tablet">Tablet</option>
            <option value="Capsule">Capsule</option>
            <option value="Liquid">Liquid</option>
          </select>
        </div>
        {/* Add similar divs for other form fields */}
        <button type="submit">Add Medicine</button>
      </form>
    </div>
  );
};

export default MedsForm;
